import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import * as tf from '@tensorflow/tfjs';
import * as posedetection from '@tensorflow-models/pose-detection';
import '@tensorflow/tfjs-backend-webgl';
import './ExercisePage.css';
import { Chart as ChartJS, LineElement, PointElement, CategoryScale, LinearScale } from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(LineElement, PointElement, CategoryScale, LinearScale);

const exerciseData = {
  cardiac: ['Chair Marching', 'Leg Extensions', 'Step-Ups'],
  neuro: ['Balance Training', 'Gait Training', 'Coordination'],
  ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
  physio: ['Arm Circles', 'Shoulder Rolls', 'Hydration Tips'],
};

function ExercisePage() {
  const { type } = useParams();
  const [selectedExercise, setSelectedExercise] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [showGraph, setShowGraph] = useState(false);
  const [poseScores, setPoseScores] = useState([]);
  const [repCount, setRepCount] = useState(0);
  const [timer, setTimer] = useState(0);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const detectorRef = useRef(null);
  const intervalRef = useRef(null);
  const synthRef = useRef(window.speechSynthesis);

  const exercises = exerciseData[type] || [];

  // Start Timer
  useEffect(() => {
    let timerId;
    if (isScanning) {
      timerId = setInterval(() => {
        setTimer(prev => prev + 1);
      }, 1000);
    }
    return () => clearInterval(timerId);
  }, [isScanning]);

  // Load Pose Detector
  useEffect(() => {
    const loadModel = async () => {
      await tf.setBackend('webgl');
      detectorRef.current = await posedetection.createDetector(posedetection.SupportedModels.MoveNet, {
        modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING,
      });
    };
    loadModel();
  }, []);

  const speak = (text) => {
    if (!synthRef.current.speaking) {
      const utterance = new SpeechSynthesisUtterance(text);
      synthRef.current.speak(utterance);
    }
  };

  const startPoseDetection = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    videoRef.current.srcObject = stream;
    await videoRef.current.play();

    const detect = async () => {
      if (!isScanning || !detectorRef.current) return;
      const poses = await detectorRef.current.estimatePoses(videoRef.current);
      drawCanvas(poses);
      const score = poses[0]?.keypoints.reduce((acc, k) => acc + k.score, 0) / 17 || 0;
      setPoseScores(prev => [...prev.slice(-20), score]);

      // Voice feedback
      if (score < 0.4) speak('Please correct your posture');

      // Simple rep count logic (e.g. above threshold means 1 rep)
      if (score > 0.6) setRepCount(prev => prev + 1);

      requestAnimationFrame(detect);
    };

    detect();
  };

  const drawCanvas = (poses) => {
    const ctx = canvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    poses.forEach(pose => {
      pose.keypoints.forEach(keypoint => {
        if (keypoint.score > 0.3) {
          ctx.beginPath();
          ctx.arc(keypoint.x, keypoint.y, 5, 0, 2 * Math.PI);
          ctx.fillStyle = 'lime';
          ctx.fill();
        }
      });
    });
  };
  const PoseDetector = ({ selectedExercise }) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [repCount, setRepCount] = useState(0);
  const [prevPoseState, setPrevPoseState] = useState("standing");
  const synthRef = useRef(window.speechSynthesis);

  const speak = (msg) => {
    if (synthRef.current.speaking) return;
    const utter = new SpeechSynthesisUtterance(msg);
    synthRef.current.speak(utter);
  };

  const getAngle = (a, b, c) => {
    const ab = Math.sqrt((a.x - b.x) ** 2 + (a.y - b.y) ** 2);
    const bc = Math.sqrt((b.x - c.x) ** 2 + (b.y - c.y) ** 2);
    const ac = Math.sqrt((a.x - c.x) ** 2 + (a.y - c.y) ** 2);
    return Math.acos((ab ** 2 + bc ** 2 - ac ** 2) / (2 * ab * bc)) * (180 / Math.PI);
  };

  useEffect(() => {
    let detector;

    const runPoseDetection = async () => {
      await tf.setBackend("webgl");
      detector = await posedetection.createDetector(posedetection.SupportedModels.MoveNet, {
        modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING,
      });

      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();

      const detect = async () => {
        const poses = await detector.estimatePoses(videoRef.current);
        drawPose(poses);

        if (selectedExercise === "Chair Squats" && poses[0]) {
          const keypoints = poses[0].keypoints;

          const leftHip = keypoints[11];
          const leftKnee = keypoints[13];
          const leftAnkle = keypoints[15];

          if (
            leftHip.score > 0.4 &&
            leftKnee.score > 0.4 &&
            leftAnkle.score > 0.4
          ) {
            const angle = getAngle(leftHip, leftKnee, leftAnkle);

            if (angle < 100 && prevPoseState === "standing") {
              setPrevPoseState("squatting");
              speak("Good, now stand up");
            } else if (angle > 160 && prevPoseState === "squatting") {
              setPrevPoseState("standing");
              setRepCount((prev) => prev + 1);
              speak("One rep complete");
            } else if (angle > 100 && angle < 160) {
              speak("Keep going");
            }
          }
        }

        requestAnimationFrame(detect);
      };

      detect();
    };

    const drawPose = (poses) => {
      const ctx = canvasRef.current.getContext("2d");
      ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

      poses.forEach((pose) => {
        pose.keypoints.forEach((keypoint) => {
          if (keypoint.score > 0.4) {
            ctx.beginPath();
            ctx.arc(keypoint.x, keypoint.y, 5, 0, 2 * Math.PI);
            ctx.fillStyle = "lime";
            ctx.fill();
          }
        });
      });
    };

    runPoseDetection();
  }, [selectedExercise, prevPoseState]);

  return (
    <div className="relative">
      <video ref={videoRef} className="w-full h-auto" width="640" height="480" />
      <canvas ref={canvasRef} className="absolute top-0 left-0" width="640" height="480" />
      <div className="mt-2 text-lg text-center font-semibold">Reps: {repCount}</div>
    </div>
  );
};


  const handleStart = () => {
    if (!selectedExercise) return alert('Please select an exercise!');
    setIsScanning(true);
    setRepCount(0);
    setTimer(0);
    startPoseDetection();
  };

  const handleStop = () => {
    setIsScanning(false);
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
    console.log("Session ended");
  };

  const handleSave = () => {
    console.log('Session saved');
  };

  return (
    <div className="exercise-container">
      <header className="top-bar">AI-Rehab – {selectedExercise || 'Select Exercise'}</header>

      <div className="main-layout">
        <aside className="left-panel">
          {exercises.map(ex => (
            <button key={ex} className={`exercise-button ${selectedExercise === ex ? 'active' : ''}`} onClick={() => {
              setSelectedExercise(ex);
              setShowGraph(false);
            }}>
              {ex}
            </button>
          ))}
          <button className="exercise-button graph-button" onClick={() => setShowGraph(true)}>
            View Graph
          </button>
        </aside>

        <main className="content-panel">
          {showGraph ? (
            <div className="canvas-placeholder">
              <Line
                data={{
                  labels: poseScores.map((_, i) => i + 1),
                  datasets: [{
                    label: 'Pose Score',
                    data: poseScores,
                    fill: false,
                    borderColor: 'rgb(75, 192, 192)',
                  }],
                }}
                options={{ responsive: true, scales: { y: { min: 0, max: 1 } } }}
              />
            </div>
          ) : (
            <>
              <div className="canvas-placeholder">
                <video ref={videoRef} width="640" height="480" className="absolute" />
                <canvas ref={canvasRef} width="640" height="480" className="absolute" />
              </div>

              <div className="controls">
                <button className="control-btn start" onClick={handleStart}>Start</button>
                <button className="control-btn stop" onClick={handleStop}>Stop</button>
                <button className="control-btn save" onClick={handleSave}>Save</button>
              </div>

              <div className="stats">
                <p>🕒 Timer: {timer}s</p>
                <p>🔁 Reps: {repCount}</p>
              </div>
            </>
          )}
        </main>
      </div>
    </div>
  );
}

export default ExercisePage;
