import React, { useRef, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import * as tf from '@tensorflow/tfjs';
import * as posedetection from '@tensorflow-models/pose-detection';
import '@tensorflow/tfjs-backend-webgl';
import './ExercisePage.css';
import { ChairSquatTracker } from '../exercises/ChairSquats';

// Skeleton connections for MoveNet
const CONNECTIONS = [
  ['left_shoulder', 'right_shoulder'],
  ['left_shoulder', 'left_hip'],
  ['right_shoulder', 'right_hip'],
  ['left_hip', 'right_hip'],
  ['left_shoulder', 'left_elbow'],
  ['left_elbow', 'left_wrist'],
  ['right_shoulder', 'right_elbow'],
  ['right_elbow', 'right_wrist'],
  ['left_hip', 'left_knee'],
  ['left_knee', 'left_ankle'],
  ['right_hip', 'right_knee'],
  ['right_knee', 'right_ankle'],
];

const exerciseMap = {
  cardiac: ['Chair Marching', 'Leg Extensions', 'Step-Ups'],
  neuro: ['Balance Training', 'Gait Training', 'Coordination'],
  ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
};

const FPS = 10; // Frames per second for detection

const ExercisePage = () => {
  const { type } = useParams();
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const detectorRef = useRef(null);
  const requestRef = useRef(null);
  const trackerRef = useRef(null);
  const lastTimeRef = useRef(0);

  const [selectedExercise, setSelectedExercise] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [repCount, setRepCount] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [error, setError] = useState(null);

  const exercises = exerciseMap[type] || [];

  useEffect(() => {
    // Initialize TensorFlow
    const initTF = async () => {
      try {
        await tf.setBackend('webgl');
        await tf.ready();
      } catch (err) {
        console.error('TensorFlow initialization failed:', err);
        setError('Failed to initialize TensorFlow. Please try refreshing the page.');
      }
    };

    initTF();

    return () => {
      // Cleanup on unmount
      stopScan();
      if (detectorRef.current) {
        detectorRef.current.dispose();
      }
    };
  }, []);

  const drawKeypoints = (pose) => {
    if (!canvasRef.current || !pose) return;

    const ctx = canvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    // Draw skeleton connections
    ctx.strokeStyle = 'white';
    ctx.lineWidth = 2;

    CONNECTIONS.forEach(([joint1, joint2]) => {
      const kp1 = pose.keypoints.find(kp => kp.name === joint1);
      const kp2 = pose.keypoints.find(kp => kp.name === joint2);

      if (kp1 && kp2 && kp1.score > 0.4 && kp2.score > 0.4) {
        ctx.beginPath();
        ctx.moveTo(kp1.x, kp1.y);
        ctx.lineTo(kp2.x, kp2.y);
        ctx.stroke();
      }
    });

    // Draw keypoints
    pose.keypoints.forEach((kp) => {
      if (kp.score > 0.4) {
        ctx.beginPath();
        ctx.arc(kp.x, kp.y, 5, 0, 2 * Math.PI);
        ctx.fillStyle = 'lime';
        ctx.fill();
      }
    });
  };

  const runDetection = async (timestamp) => {
    if (!isScanning || !videoRef.current || !canvasRef.current || !detectorRef.current) {
      return;
    }

    // Throttle detection to target FPS
    if (timestamp - lastTimeRef.current > 1000 / FPS) {
      lastTimeRef.current = timestamp;

      try {
        const poses = await detectorRef.current.estimatePoses(videoRef.current);

        if (poses && poses.length > 0) {
          drawKeypoints(poses[0]);

          if (selectedExercise === 'Chair Squats' && trackerRef.current) {
            const result = trackerRef.current.process(poses[0].keypoints);
            setRepCount(result.reps);
            setFeedback(result.feedback);
          }
        }
      } catch (err) {
        console.error('Detection error:', err);
        setFeedback('Error during pose detection');
      }
    }

    requestRef.current = requestAnimationFrame(runDetection);
  };

  const startScan = async () => {
    if (!selectedExercise) {
      setFeedback('Please select an exercise first');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      // Initialize exercise tracker
      if (selectedExercise === 'Chair Squats') {
        trackerRef.current = new ChairSquatTracker();
      }

      // Initialize pose detector
      const detector = await posedetection.createDetector(
        posedetection.SupportedModels.MoveNet,
        {
          modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING,
        }
      );
      detectorRef.current = detector;

      // Get camera stream
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: 640, 
          height: 480, 
          facingMode: 'user' 
        } 
      });

      videoRef.current.srcObject = stream;
      await videoRef.current.play();

      // Reset counters and start detection
      if (trackerRef.current) {
        trackerRef.current.reset();
      }
      setRepCount(0);
      setFeedback('Starting detection...');
      setIsScanning(true);
      lastTimeRef.current = 0;
      requestRef.current = requestAnimationFrame(runDetection);

    } catch (err) {
      console.error('Start scan error:', err);
      setError(`Failed to start: ${err.message}`);
      stopScan();
    } finally {
      setIsLoading(false);
    }
  };

  const stopScan = () => {
    setIsScanning(false);
    cancelAnimationFrame(requestRef.current);

    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }

    const ctx = canvasRef.current?.getContext('2d');
    ctx?.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    setFeedback('Detection stopped');
  };

  const saveData = () => {
    alert(`Saved ${repCount} reps for ${selectedExercise}`);
    // Here you would typically send data to your backend
  };

  return (
    <div className="exercise-container">
      <header className="top-bar">
        AI-Rehab â€“ {selectedExercise || 'Select Exercise'}
      </header>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <div className="main-layout">
        <aside className="left-panel">
          <h3>Exercises</h3>
          {exercises.map((exercise) => (
            <button
              key={exercise}
              className={`exercise-button ${selectedExercise === exercise ? 'active' : ''}`}
              onClick={() => {
                setSelectedExercise(exercise);
                setRepCount(0);
                setFeedback('');
                stopScan();
              }}
              disabled={isScanning}
            >
              {exercise}
            </button>
          ))}
        </aside>

        <main className="content-panel">
          <div className="canvas-area">
            <video 
              ref={videoRef} 
              className="video-feed" 
              width="640" 
              height="480" 
              playsInline
            />
            <canvas 
              ref={canvasRef} 
              className="canvas-overlay" 
              width="640" 
              height="480" 
            />
          </div>

          <div className="feedback-section">
            <p><strong>Feedback:</strong> {feedback || 'No feedback yet'}</p>
            <p><strong>Reps:</strong> {repCount}</p>
          </div>

          <div className="controls">
            <button 
              className="control-btn start" 
              onClick={startScan} 
              disabled={isScanning || isLoading || !selectedExercise}
            >
              {isLoading ? 'Initializing...' : 'Start Scan'}
            </button>
            <button 
              className="control-btn stop" 
              onClick={stopScan} 
              disabled={!isScanning}
            >
              Stop Scan
            </button>
            <button 
              className="control-btn save" 
              onClick={saveData}
              disabled={repCount === 0}
            >
              Save
            </button>
          </div>
        </main>
      </div>
    </div>
  );
};

export default ExercisePage;
