// src/pages/ExercisePage.js

import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ExercisePage.css';
import * as tf from '@tensorflow/tfjs';
import * as posedetection from '@tensorflow-models/pose-detection';
import '@tensorflow/tfjs-backend-webgl';
import { processChairSquat, resetChairSquat } from '../exercises/ChairSquats';

const exerciseData = {
  cardiac: ['Chair Squats'],
  neuro: ['Chair Squats'],
  ortho: ['Chair Squats'],
  physio: ['Chair Squats'],
};

function ExercisePage() {
  const { type } = useParams();
  const exercises = exerciseData[type] || [];

  const [selectedExercise, setSelectedExercise] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [poseStatus, setPoseStatus] = useState('');
  const [repCount, setRepCount] = useState(0);
  const [timer, setTimer] = useState(0);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const detectorRef = useRef(null);
  const rafId = useRef(null);
  const timerInterval = useRef(null);
  const synthRef = useRef(window.speechSynthesis);

  // Voice Feedback
  const speak = (text) => {
    if (synthRef.current.speaking) return;
    const utterance = new SpeechSynthesisUtterance(text);
    synthRef.current.speak(utterance);
  };

  // Pose Detection
  const detect = async () => {
    if (
      !isScanning ||
      !videoRef.current ||
      videoRef.current.readyState !== 4 ||
      !detectorRef.current
    ) {
      rafId.current = requestAnimationFrame(detect);
      return;
    }

    const poses = await detectorRef.current.estimatePoses(videoRef.current);
    drawResults(poses);

    if (poses.length && selectedExercise === 'Chair Squats') {
      const { isCorrect, count, feedback } = processChairSquat(poses[0]);
      if (count > repCount) setRepCount(count);
      setPoseStatus(feedback);

      if (!isCorrect) speak('Posture incorrect');
      else speak('Posture correct');
    }

    rafId.current = requestAnimationFrame(detect);
  };

  // Drawing Keypoints
  const drawResults = (poses) => {
    const ctx = canvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    poses.forEach((pose) => {
      pose.keypoints.forEach((keypoint) => {
        if (keypoint.score > 0.4) {
          ctx.beginPath();
          ctx.arc(keypoint.x, keypoint.y, 5, 0, 2 * Math.PI);
          ctx.fillStyle = 'lime';
          ctx.fill();
        }
      });
    });
  };

  // Start Scanning
  const handleStart = async () => {
    if (!selectedExercise) return alert('Select an exercise first!');
    setIsScanning(true);
    resetChairSquat();
    setRepCount(0);
    setPoseStatus('');
    setTimer(0);

    await tf.setBackend('webgl');
    detectorRef.current = await posedetection.createDetector(
      posedetection.SupportedModels.MoveNet,
      { modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING }
    );

    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    videoRef.current.srcObject = stream;
    await videoRef.current.play();

    rafId.current = requestAnimationFrame(detect);
    timerInterval.current = setInterval(() => {
      setTimer((prev) => prev + 1);
    }, 1000);
  };

  // Stop Scanning
  const handleStop = () => {
    setIsScanning(false);
    if (rafId.current) cancelAnimationFrame(rafId.current);
    if (timerInterval.current) clearInterval(timerInterval.current);
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach((t) => t.stop());
      videoRef.current.srcObject = null;
    }
  };

  const handleSave = () => {
    alert(`Saved: ${repCount} reps in ${timer} sec`);
  };

  return (
    <div className="exercise-container">
      <header className="top-bar">AI-Rehab ‚Äì {selectedExercise || 'Select Exercise'}</header>

      <div className="main-layout">
        <aside className="left-panel">
          {exercises.map((exercise) => (
            <button
              key={exercise}
              className={`exercise-button ${selectedExercise === exercise ? 'active' : ''}`}
              onClick={() => setSelectedExercise(exercise)}
            >
              {exercise}
            </button>
          ))}
        </aside>

        <main className="content-panel">
          <div className="canvas-placeholder">
            <video ref={videoRef} width="640" height="480" className="hidden" />
            <canvas ref={canvasRef} width="640" height="480" />
          </div>

          <div className="controls">
            <button className="control-btn start" onClick={handleStart}>
              Start
            </button>
            <button className="control-btn stop" onClick={handleStop}>
              Stop
            </button>
            <button className="control-btn save" onClick={handleSave}>
              Save
            </button>
          </div>

          <div className="status">
            <p>üß† Pose Status: {poseStatus}</p>
            <p>üîÅ Reps: {repCount}</p>
            <p>‚è± Time: {timer}s</p>
          </div>
        </main>
      </div>
    </div>
  );
}

export default ExercisePage;
