let isReaching = false;
let lastSpeakTime = 0;

export function resetArmReachTouch() {
  isReaching = false;
  lastSpeakTime = 0;
}

export function processArmReachTouch(landmarks) {
  const now = Date.now();

  const leftWrist = landmarks[15];
  const rightWrist = landmarks[16];
  const nose = landmarks[0];

  if (!leftWrist || !rightWrist || !nose) {
    return { speak: 'Make sure full body is visible' };
  }

  const isTouchingFace =
    Math.abs(leftWrist.x - nose.x) < 0.08 && Math.abs(leftWrist.y - nose.y) < 0.08;

  if (isTouchingFace && !isReaching) {
    isReaching = true;
    return { speak: 'Good reach', rep: 1 };
  }

  if (!isTouchingFace && isReaching) {
    isReaching = false;
    return { speak: 'Return to start' };
  }

  if (!isTouchingFace && now - lastSpeakTime > 4000) {
    lastSpeakTime = now;
    return { speak: 'Try reaching towards your face' };
  }

  return null;
}
