let lastState = 'retracted';
let lastSpeak = 0;

export function resetSeatedArmPunch() {
  lastState = 'retracted';
  lastSpeak = 0;
}

export function processSeatedArmPunch(landmarks) {
  const now = Date.now();
  const leftWristZ = landmarks[15].z;
  const rightWristZ = landmarks[16].z;

  const leftExtended = leftWristZ < -0.1;
  const rightExtended = rightWristZ < -0.1;

  if ((leftExtended || rightExtended) && lastState === 'retracted') {
    lastState = 'extended';
    return { speak: 'Good punch', rep: 1 };
  }

  if (!leftExtended && !rightExtended && lastState === 'extended') {
    lastState = 'retracted';
  }

  if (!leftExtended && !rightExtended && now - lastSpeak > 4000) {
    lastSpeak = now;
    return { speak: 'Extend your arms forward' };
  }

  return null;
}
