// src/pages/ExercisePage.js
import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ExercisePage.css';
import * as tf from '@tensorflow/tfjs';
import * as posedetection from '@tensorflow-models/pose-detection';
import '@tensorflow/tfjs-backend-webgl';

const exerciseData = {
  cardiac: ['Chair Marching', 'Leg Extensions', 'Step-Ups'],
  neuro: ['Balance Training', 'Gait Training', 'Coordination'],
  ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
  physio: ['Arm Circles', 'Shoulder Rolls', 'Hydration Tips'],
};

const PoseDetector = ({ isScanning }) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const detectorRef = useRef(null);
  const speechRef = useRef(null);
  const [poseScore, setPoseScore] = useState(null);

  useEffect(() => {
    if (!isScanning) return;

    const init = async () => {
      await tf.setBackend('webgl');
      detectorRef.current = await posedetection.createDetector(posedetection.SupportedModels.MoveNet, {
        modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING,
      });

      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
        videoRef.current.onloadeddata = detectPose;
      }
    };

    const detectPose = async () => {
      if (!detectorRef.current || !videoRef.current) return;
      const poses = await detectorRef.current.estimatePoses(videoRef.current);
      if (poses.length) {
        const avgScore =
          poses[0].keypoints.reduce((sum, kp) => sum + kp.score, 0) / poses[0].keypoints.length;
        setPoseScore(avgScore.toFixed(2));
        drawPose(poses);
        handleVoiceFeedback(avgScore);
      }
      requestAnimationFrame(detectPose);
    };

    const drawPose = (poses) => {
      const ctx = canvasRef.current.getContext('2d');
      ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
      poses[0].keypoints.forEach((kp) => {
        if (kp.score > 0.4) {
          ctx.beginPath();
          ctx.arc(kp.x, kp.y, 5, 0, 2 * Math.PI);
          ctx.fillStyle = 'lime';
          ctx.fill();
        }
      });
    };

    const handleVoiceFeedback = (score) => {
      if (score < 0.7) {
        if (!speechRef.current || speechRef.current.text !== 'Please correct your posture') {
          speechRef.current = new SpeechSynthesisUtterance('Please correct your posture');
          speechSynthesis.cancel(); // Stop previous
          speechSynthesis.speak(speechRef.current);
        }
      } else {
        speechSynthesis.cancel();
        speechRef.current = null;
      }
    };

    init();

    return () => {
      speechSynthesis.cancel();
      if (videoRef.current?.srcObject) {
        videoRef.current.srcObject.getTracks().forEach(track => track.stop());
      }
    };
  }, [isScanning]);

  return (
    <div className="relative">
      <video ref={videoRef} className="w-full h-auto" width="640" height="480" />
      <canvas
        ref={canvasRef}
        className="absolute top-0 left-0"
        width="640"
        height="480"
      />
      {poseScore && (
        <div className="absolute top-2 left-2 bg-black text-white px-3 py-1 rounded text-sm">
          Pose Score: {poseScore}
        </div>
      )}
    </div>
  );
};

function ExercisePage() {
  const { type } = useParams();
  const [selectedExercise, setSelectedExercise] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [showGraph, setShowGraph] = useState(false);

  const exercises = exerciseData[type] || [];

  const handleStart = () => {
    if (!selectedExercise) return alert('Select an exercise first!');
    setIsScanning(true);
  };

  const handleStop = () => {
    setIsScanning(false);
    speechSynthesis.cancel(); // stop voice if running
  };

  return (
    <div className="exercise-container">
      <header className="top-bar">AI-Rehab – {selectedExercise || 'Select Exercise'}</header>

      <div className="main-layout">
        <aside className="left-panel">
          {exercises.map((exercise) => (
            <button
              key={exercise}
              className={`exercise-button ${selectedExercise === exercise ? 'active' : ''}`}
              onClick={() => {
                setSelectedExercise(exercise);
                setShowGraph(false);
              }}
            >
              {exercise}
            </button>
          ))}
          <button
            className="exercise-button graph-button"
            onClick={() => {
              setShowGraph(true);
              setSelectedExercise('');
              setIsScanning(false);
              speechSynthesis.cancel();
            }}
          >
            View Graph
          </button>
        </aside>

        <main className="content-panel">
          {showGraph ? (
            <div className="canvas-placeholder graph-view">
              <p>[ 📈 Progress Graph Placeholder ]</p>
            </div>
          ) : (
            <div className="canvas-placeholder">
              {isScanning && <PoseDetector isScanning={isScanning} />}
            </div>
          )}

          {!showGraph && (
            <div className="controls">
              <button className="control-btn start" onClick={handleStart}>Start Scan</button>
              <button className="control-btn stop" onClick={handleStop}>Stop Scan</button>
              <button className="control-btn save" onClick={() => alert('Data Saved')}>Save</button>
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

export default ExercisePage;
