import React, { useEffect, useRef, useState } from 'react';
import { useParams } from 'react-router-dom';
import './ExercisePage.css';
import * as tf from '@tensorflow/tfjs';
import * as posedetection from '@tensorflow-models/pose-detection';
import '@tensorflow/tfjs-backend-webgl';
import { Line } from 'react-chartjs-2';
import { processChairSquat, resetChairSquat } from '../exercises/ChairSquats';

const exerciseData = {
  cardiac: ['Chair Marching', 'Leg Extensions', 'Step-Ups'],
  neuro: ['Balance Training', 'Gait Training', 'Coordination'],
  ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
  physio: ['Arm Circles', 'Shoulder Rolls', 'Hydration Tips'],
};

function ExercisePage() {
  const { type } = useParams();
  const [selectedExercise, setSelectedExercise] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [showGraph, setShowGraph] = useState(false);
  const [reps, setReps] = useState(0);
  const [poseData, setPoseData] = useState([]);
  const [voiceFeedback, setVoiceFeedback] = useState('');
  const exercises = exerciseData[type] || [];

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const detectorRef = useRef(null);
  const animationFrameRef = useRef(null);

  const speak = (text) => {
    const synth = window.speechSynthesis;
    const utter = new SpeechSynthesisUtterance(text);
    synth.cancel(); // Prevent overlapping
    synth.speak(utter);
  };

  useEffect(() => {
    if (voiceFeedback) speak(voiceFeedback);
  }, [voiceFeedback]);

  const runDetection = async () => {
    await tf.setBackend('webgl');
    detectorRef.current = await posedetection.createDetector(posedetection.SupportedModels.MoveNet, {
      modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING,
    });

    if (navigator.mediaDevices.getUserMedia) {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();

      videoRef.current.onloadeddata = () => detectFrame();
    }
  };

  const detectFrame = async () => {
    if (!detectorRef.current) return;

    const poses = await detectorRef.current.estimatePoses(videoRef.current);
    drawResults(poses);

    if (poses.length > 0 && selectedExercise === 'Chair Squats') {
      processChairSquat(poses[0], setReps, setVoiceFeedback);
      setPoseData((prev) => [...prev.slice(-19), reps]);
    }

    animationFrameRef.current = requestAnimationFrame(detectFrame);
  };

  const drawResults = (poses) => {
    const ctx = canvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    poses.forEach((pose) => {
      pose.keypoints.forEach((kp) => {
        if (kp.score > 0.4) {
          ctx.beginPath();
          ctx.arc(kp.x, kp.y, 5, 0, 2 * Math.PI);
          ctx.fillStyle = 'lime';
          ctx.fill();
        }
      });
    });
  };

  const handleStart = async () => {
    if (!selectedExercise) return alert('Select an exercise first!');
    setIsScanning(true);
    resetChairSquat();
    setReps(0);
    setPoseData([]);
    await runDetection();
  };

  const handleStop = () => {
    setIsScanning(false);
    cancelAnimationFrame(animationFrameRef.current);
    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach((track) => track.stop());
    }
    detectorRef.current = null;
  };

  const handleSave = () => {
    console.log('Progress saved:', poseData);
  };

  const graphData = {
    labels: poseData.map((_, i) => i + 1),
    datasets: [
      {
        label: 'Repetitions',
        data: poseData,
        borderColor: '#2563eb',
        backgroundColor: 'rgba(37, 99, 235, 0.3)',
        fill: true,
      },
    ],
  };

  return (
    <div className="exercise-container">
      <header className="top-bar">AI-Rehab â€“ {selectedExercise || 'Select Exercise'}</header>

      <div className="main-layout">
        <aside className="left-panel">
          {exercises.map((ex) => (
            <button
              key={ex}
              className={`exercise-button ${selectedExercise === ex ? 'active' : ''}`}
              onClick={() => {
                setSelectedExercise(ex);
                setShowGraph(false);
                setReps(0);
                setPoseData([]);
              }}
            >
              {ex}
            </button>
          ))}
          <button className="exercise-button graph-button" onClick={() => setShowGraph(true)}>
            View Graph
          </button>
        </aside>

        <main className="content-panel">
          {showGraph ? (
            <div className="canvas-placeholder graph-view">
              <Line data={graphData} />
            </div>
          ) : (
            <div className="canvas-placeholder">
              <video ref={videoRef} className="w-full h-auto" width="640" height="480" />
              <canvas ref={canvasRef} className="absolute top-0 left-0" width="640" height="480" />
            </div>
          )}

          {!showGraph && (
            <div className="controls">
              <button className="control-btn start" onClick={handleStart}>
                Start Scan
              </button>
              <button className="control-btn stop" onClick={handleStop}>
                Stop Scan
              </button>
              <button className="control-btn save" onClick={handleSave}>
                Save
              </button>
              <div className="reps-info">Reps: {reps}</div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

export default ExercisePage;
