import React, { useEffect, useRef, useState } from 'react';
import { useParams } from 'react-router-dom';
import './ExercisePage.css';
import * as tf from '@tensorflow/tfjs';
import * as posedetection from '@tensorflow-models/pose-detection';
import '@tensorflow/tfjs-backend-webgl';
import { processChairSquat, resetChairSquat } from '../exercises/ChairSquats';

const exerciseMap = {
  cardiac: ['Chair Marching', 'Leg Extensions', 'Step-Ups'],
  neuro: ['Balance Training', 'Gait Training', 'Coordination'],
  ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
};

const ExercisePage = () => {
  const { type } = useParams();
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const detectorRef = useRef(null);
  const requestRef = useRef(null);

  const [selectedExercise, setSelectedExercise] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [repCount, setRepCount] = useState(0);
  const [feedback, setFeedback] = useState('');

  const synthRef = useRef(window.speechSynthesis);

  const exercises = exerciseMap[type] || [];

  useEffect(() => {
    tf.setBackend('webgl');
    return () => stopScan(); // Cleanup
  }, []);

  const speak = (text) => {
    if (!synthRef.current) return;
    const utterance = new SpeechSynthesisUtterance(text);
    synthRef.current.cancel(); // cancel previous
    synthRef.current.speak(utterance);
  };

  const drawKeypoints = (poses) => {
    const ctx = canvasRef.current?.getContext('2d');
    if (!ctx) return;
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    poses.forEach((pose) => {
      pose.keypoints.forEach((kp) => {
        if (kp.score > 0.4) {
          ctx.beginPath();
          ctx.arc(kp.x, kp.y, 5, 0, 2 * Math.PI);
          ctx.fillStyle = 'lime';
          ctx.fill();
        }
      });
    });
  };

  const runDetection = async () => {
    if (!videoRef.current || !canvasRef.current || !detectorRef.current) return;

    const poses = await detectorRef.current.estimatePoses(videoRef.current);
    drawKeypoints(poses);

    if (poses[0]?.keypoints) {
      const keypoints = poses[0].keypoints;

      // ✅ Exercise-specific logic
      if (selectedExercise === 'Chair Squats') {
        const result = processChairSquat(keypoints);
        if (result.feedback) {
          setFeedback(result.feedback);
        }
        if (result.reps !== repCount) {
          setRepCount(result.reps);
        }
      }
    }

    requestRef.current = requestAnimationFrame(runDetection);
  };

  const startScan = async () => {
    if (!selectedExercise) return alert('Please select an exercise first');

    setIsScanning(true);
    speak(`Starting ${selectedExercise}`);

    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    videoRef.current.srcObject = stream;
    await videoRef.current.play();

    const detector = await posedetection.createDetector(posedetection.SupportedModels.MoveNet, {
      modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING,
    });

    detectorRef.current = detector;
    runDetection();
  };

  const stopScan = () => {
    setIsScanning(false);
    if (requestRef.current) cancelAnimationFrame(requestRef.current);

    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach((track) => track.stop());
    }

    const ctx = canvasRef.current?.getContext('2d');
    ctx?.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    resetChairSquat();
    setRepCount(0);
    setFeedback('');
    speak('Scan stopped');
  };

  const saveData = () => {
    speak(`You completed ${repCount} reps of ${selectedExercise}`);
    console.log(`Saved data: ${repCount} reps of ${selectedExercise}`);
  };

  return (
    <div className="exercise-container">
      <header className="top-bar">AI-Rehab – {selectedExercise || 'Select Exercise'}</header>

      <div className="main-layout">
        <aside className="left-panel">
          {exercises.map((exercise) => (
            <button
              key={exercise}
              className={`exercise-button ${selectedExercise === exercise ? 'active' : ''}`}
              onClick={() => {
                setSelectedExercise(exercise);
                resetChairSquat();
                setRepCount(0);
                setFeedback('');
              }}
            >
              {exercise}
            </button>
          ))}
        </aside>

        <main className="content-panel">
          <div className="canvas-area">
            <video ref={videoRef} className="video-feed" width="640" height="480" />
            <canvas ref={canvasRef} className="canvas-overlay" width="640" height="480" />
          </div>

          <div className="feedback">
            {selectedExercise === 'Chair Squats' && (
              <>
                <p><strong>Reps:</strong> {repCount}</p>
                <p><strong>Feedback:</strong> {feedback}</p>
              </>
            )}
          </div>

          <div className="controls">
            <button className="control-btn start" onClick={startScan} disabled={isScanning}>
              Start
            </button>
            <button className="control-btn stop" onClick={stopScan} disabled={!isScanning}>
              Stop
            </button>
            <button className="control-btn save" onClick={saveData}>
              Save
            </button>
          </div>
        </main>
      </div>
    </div>
  );
};

export default ExercisePage;
