import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import * as tf from '@tensorflow/tfjs';
import * as posedetection from '@tensorflow-models/pose-detection';
import '@tensorflow/tfjs-backend-webgl';
import Chart from 'chart.js/auto';
import './ExercisePage.css';

import { processChairSquat, resetChairSquat } from '../exercises/ChairSquats';

const exerciseData = {
  cardiac: ['Chair Marching', 'Leg Extensions', 'Step-Ups'],
  neuro: ['Balance Training', 'Gait Training', 'Coordination'],
  ortho: ['Chair Squats'],
  physio: ['Arm Circles', 'Shoulder Rolls'],
};

const ExercisePage = () => {
  const { type } = useParams();
  const exercises = exerciseData[type] || [];

  const [selectedExercise, setSelectedExercise] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [reps, setReps] = useState(0);
  const [showGraph, setShowGraph] = useState(false);
  const [chartInstance, setChartInstance] = useState(null);
  const [scanStartTime, setScanStartTime] = useState(null);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const detectorRef = useRef(null);
  const requestRef = useRef(null);
  const graphRef = useRef(null);

  useEffect(() => {
    return () => {
      stopScan();
    };
  }, []);

  const startScan = async () => {
    if (!selectedExercise) return alert('Please select an exercise!');
    setIsScanning(true);
    setReps(0);
    resetChairSquat();
    setScanStartTime(Date.now());

    await tf.setBackend('webgl');
    const detector = await posedetection.createDetector(posedetection.SupportedModels.MoveNet, {
      modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING,
    });
    detectorRef.current = detector;

    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    videoRef.current.srcObject = stream;

    await new Promise(resolve => {
      videoRef.current.onloadedmetadata = () => {
        resolve();
      };
    });

    detectPose();
  };

  const stopScan = () => {
    setIsScanning(false);
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
    cancelAnimationFrame(requestRef.current);
    resetChairSquat();
    window.speechSynthesis.cancel();
  };

  const detectPose = async () => {
    if (!videoRef.current || !canvasRef.current || !detectorRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const detect = async () => {
      if (!isScanning) return;

      const poses = await detectorRef.current.estimatePoses(video);
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      poses.forEach(pose => {
        pose.keypoints.forEach(kp => {
          if (kp.score > 0.5) {
            ctx.beginPath();
            ctx.arc(kp.x, kp.y, 5, 0, 2 * Math.PI);
            ctx.fillStyle = 'lime';
            ctx.fill();
          }
        });

        if (selectedExercise === 'Chair Squats') {
          const result = processChairSquat(pose.keypoints);
          setReps(result.reps);
          updateChart(result.reps);
        }
      });

      requestRef.current = requestAnimationFrame(detect);
    };

    detect();
  };

  const updateChart = (repCount) => {
    const elapsedSeconds = Math.floor((Date.now() - scanStartTime) / 1000);

    if (chartInstance) {
      chartInstance.data.labels.push(`${elapsedSeconds}s`);
      chartInstance.data.datasets[0].data.push(repCount);
      chartInstance.update();
    }
  };

  const initChart = () => {
    const ctx = graphRef.current.getContext('2d');
    const newChart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: [],
        datasets: [{
          label: 'Repetitions',
          data: [],
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.4,
        }],
      },
    });
    setChartInstance(newChart);
  };

  useEffect(() => {
    if (showGraph && !chartInstance) {
      initChart();
    }
  }, [showGraph]);

  return (
    <div className="exercise-container">
      <header className="top-bar">AI-Rehab â€“ {selectedExercise || 'Select Exercise'}</header>

      <div className="main-layout">
        <aside className="left-panel">
          {exercises.map(ex => (
            <button
              key={ex}
              className={`exercise-button ${selectedExercise === ex ? 'active' : ''}`}
              onClick={() => {
                setSelectedExercise(ex);
                setShowGraph(false);
              }}
            >
              {ex}
            </button>
          ))}
          <button className="exercise-button graph-button" onClick={() => setShowGraph(true)}>
            View Graph
          </button>
        </aside>

        <main className="content-panel">
          {showGraph ? (
            <canvas ref={graphRef} width={640} height={480} />
          ) : (
            <div className="video-canvas-wrap">
              <video ref={videoRef} autoPlay muted className="video" />
              <canvas ref={canvasRef} className="canvas" />
            </div>
          )}

          {!showGraph && (
            <div className="controls">
              <button onClick={startScan} className="control-btn start">Start Scan</button>
              <button onClick={stopScan} className="control-btn stop">Stop Scan</button>
              <button className="control-btn save" onClick={() => alert('Saved!')}>Save</button>
              <div className="rep-counter">Reps: {reps}</div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default ExercisePage;
