import React, { useRef, useState, useEffect } from 'react';
import * as tf from '@tensorflow/tfjs';
import * as posedetection from '@tensorflow-models/pose-detection';
import { ChairSquatTracker } from '../exercises/ChairSquats';

const ExercisePage = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const detectorRef = useRef(null);
  const trackerRef = useRef(new ChairSquatTracker());

  const [repCount, setRepCount] = useState(0);
  const [feedback, setFeedback] = useState('Select exercise to begin');
  const [isScanning, setIsScanning] = useState(false);

  useEffect(() => {
    return () => {
      stopDetection();
      if (detectorRef.current) {
        detectorRef.current.dispose();
      }
    };
  }, []);

  const startDetection = async () => {
    try {
      // Initialize camera
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { width: 640, height: 480, facingMode: 'user' }
      });
      videoRef.current.srcObject = stream;
      await videoRef.current.play();

      // Initialize pose detector
      detectorRef.current = await posedetection.createDetector(
        posedetection.SupportedModels.MoveNet,
        { modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING }
      );

      // Reset tracker
      trackerRef.current.reset();
      setRepCount(0);
      setFeedback('Starting detection...');
      setIsScanning(true);

      // Start detection loop
      detectPoses();
    } catch (err) {
      console.error("Error starting detection:", err);
      setFeedback(`Error: ${err.message}`);
    }
  };

  const detectPoses = async () => {
    if (!isScanning) return;

    try {
      // Estimate poses
      const poses = await detectorRef.current.estimatePoses(videoRef.current);

      if (poses && poses.length > 0) {
        // Process chair squats
        const result = trackerRef.current.process(poses[0].keypoints);
        setRepCount(result.reps);
        setFeedback(result.feedback);
      } else {
        setFeedback('Stand in view of the camera');
      }
    } catch (err) {
      console.error("Detection error:", err);
    }

    // Continue loop
    requestAnimationFrame(detectPoses);
  };

  const stopDetection = () => {
    setIsScanning(false);
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
    setFeedback('Detection stopped');
  };

  return (
    <div className="exercise-container">
      <div className="video-container">
        <video ref={videoRef} width="640" height="480" playsInline muted />
        <canvas ref={canvasRef} width="640" height="480" />
      </div>

      <div className="feedback">
        <p>Reps: {repCount}</p>
        <p>Feedback: {feedback}</p>
      </div>

      <div className="controls">
        <button onClick={startDetection} disabled={isScanning}>
          Start
        </button>
        <button onClick={stopDetection} disabled={!isScanning}>
          Stop
        </button>
      </div>
    </div>
  );
};

export default ExercisePage;
