import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import PoseDetector from '../components/PoseDetector';
import './ExercisePage.css';

const exerciseData = {
  cardiac: ['Chair Marching', 'Leg Extensions', 'Step-Ups'],
  neuro: ['Balance Training', 'Gait Training', 'Coordination'],
  ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
  physio: ['Arm Circles', 'Shoulder Rolls', 'Hydration Tips'],
};

function ExercisePage() {
  const { type } = useParams();
  const [selectedExercise, setSelectedExercise] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [repCount, setRepCount] = useState(0);
  const [showGraph, setShowGraph] = useState(false);

  const exercises = exerciseData[type] || [];

  const handleRepDetected = (count) => {
    setRepCount(count);
  };

  const handleStart = () => {
    if (!selectedExercise) return alert('Please select an exercise');
    setRepCount(0);
    setIsScanning(true);
  };

  const handleStop = () => {
    setIsScanning(false);
  };

  const handleSave = () => {
    alert(`Saved ${repCount} reps for ${selectedExercise}`);
  };

  return (
    <div className="exercise-container">
      <header className="top-bar">AI-Rehab â€“ {selectedExercise || 'Select Exercise'}</header>

      <div className="main-layout">
        <aside className="left-panel">
          {exercises.map((exercise) => (
            <button
              key={exercise}
              className={`exercise-button ${selectedExercise === exercise ? 'active' : ''}`}
              onClick={() => {
                setSelectedExercise(exercise);
                setShowGraph(false);
              }}
            >
              {exercise}
            </button>
          ))}
          <button
            className="exercise-button graph-button"
            onClick={() => {
              setShowGraph(true);
              setSelectedExercise('');
              setIsScanning(false);
            }}
          >
            View Graph
          </button>
        </aside>

        <main className="content-panel">
          {showGraph ? (
            <div className="canvas-placeholder graph-view">
              <p>[ðŸ“ˆ Real-time Progress Graph Placeholder]</p>
            </div>
          ) : (
            <>
              <div className="canvas-placeholder">
                <PoseDetector isActive={isScanning} onRepDetected={handleRepDetected} />
              </div>
              <div className="controls">
                <button className="control-btn start" onClick={handleStart}>Start</button>
                <button className="control-btn stop" onClick={handleStop}>Stop</button>
                <button className="control-btn save" onClick={handleSave}>Save</button>
                <p className="rep-display">Reps: {repCount}</p>
              </div>
            </>
          )}
        </main>
      </div>
    </div>
  );
}

export default ExercisePage;
