import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import './ExercisePage.css';
import PoseDetector from '../components/PoseDetector';

const exerciseData = {
  cardiac: ['Chair Marching', 'Leg Extensions', 'Step-Ups'],
  neuro: ['Balance Training', 'Gait Training', 'Coordination'],
  ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
  physio: ['Arm Circles', 'Shoulder Rolls', 'Hydration Tips'],
};

function ExercisePage() {
  const { type } = useParams();
  const [selectedExercise, setSelectedExercise] = useState('');
  const [reps, setReps] = useState(0);
  const [postureWarning, setPostureWarning] = useState('');
  const [showGraph, setShowGraph] = useState(false);

  const exercises = exerciseData[type] || [];

  const handleRepetition = () => {
    setReps((prev) => prev + 1);
  };

  const handleBadPosture = () => {
    setPostureWarning('âš ï¸ Correct your posture!');
    setTimeout(() => setPostureWarning(''), 3000);
  };

  return (
    <div className="exercise-container">
      <header className="top-bar">
        AI-Rehab â€“ {selectedExercise || 'Select Exercise'}
      </header>

      <div className="main-layout">
        <aside className="left-panel">
          {exercises.map((exercise) => (
            <button
              key={exercise}
              className={`exercise-button ${selectedExercise === exercise ? 'active' : ''}`}
              onClick={() => {
                setSelectedExercise(exercise);
                setShowGraph(false);
                setReps(0);
              }}
            >
              {exercise}
            </button>
          ))}
          <button
            className="exercise-button graph-button"
            onClick={() => {
              setShowGraph(true);
              setSelectedExercise('');
            }}
          >
            ðŸ“ˆ View Graph
          </button>
        </aside>

        <main className="content-panel">
          {showGraph ? (
            <div className="canvas-placeholder">
              <p>[ Graph Placeholder - Integrate Chart.js ]</p>
            </div>
          ) : selectedExercise ? (
            <>
              <div className="canvas-placeholder">
                {selectedExercise === 'Chair Squats' && (
                  <PoseDetector
                    onRepetition={handleRepetition}
                    onBadPosture={handleBadPosture}
                  />
                )}
              </div>
              <div className="controls">
                <p>ðŸ§® Reps: {reps}</p>
                <p className="text-red-600">{postureWarning}</p>
              </div>
            </>
          ) : (
            <p>Select an exercise from the left panel</p>
          )}
        </main>
      </div>
    </div>
  );
}

export default ExercisePage;
