// src/pages/ExercisePage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ExercisePage.css';

function ExercisePage() {
  const { type } = useParams();

  const allExercises = {
    cardiac: ['Chair Marching', 'Step-Ups', 'Leg Extensions'],
    neuro: ['Balance Training', 'Gait Training', 'Coordination'],
    ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
    physio: ['Arm Circles', 'Shoulder Rolls', 'Leg Extensions'],
  };

  const exercises = allExercises[type] || ['Chair Squats'];
  const [selectedExercise, setSelectedExercise] = useState(exercises[0]);
  const [isScanning, setIsScanning] = useState(false);

  const handleStart = () => {
    setIsScanning(true);
    console.log(`Start scanning for: ${selectedExercise}`);
  };

  const handleStop = () => {
    setIsScanning(false);
    console.log(`Stop scanning`);
  };

  const handleSave = () => {
    console.log('Data saved');
  };

  return (
    <div className="exercise-container">
      <header className="top-bar">
        <h2>ðŸ§  AI-Rehab</h2>
        <p>{selectedExercise}</p>
      </header>

      <div className="main-layout">
        {/* Left panel */}
        <aside className="left-panel">
          <h3>Exercises</h3>
          {exercises.map((exercise) => (
            <button
              key={exercise}
              className={`exercise-button ${
                selectedExercise === exercise ? 'active' : ''
              }`}
              onClick={() => setSelectedExercise(exercise)}
            >
              {exercise}
            </button>
          ))}
        </aside>

        {/* Content panel */}
        <main className="content-panel">
          <div className="canvas-placeholder">
            <p>[ Canvas/Webcam Feed for {selectedExercise} ]</p>
          </div>

          <div className="controls">
            <button className="control-btn start" onClick={handleStart}>
              Start Scan
            </button>
            <button className="control-btn stop" onClick={handleStop}>
              Stop Scan
            </button>
            <button className="control-btn save" onClick={handleSave}>
              Save
            </button>
          </div>
        </main>
      </div>

      {/* Graph section */}
      <section className="graph-section">
        <h4>ðŸ“ˆ Progress Graph (Coming Soon)</h4>
        <div className="graph-placeholder">[ Graph Placeholder ]</div>
      </section>
    </div>
  );
}

export default ExercisePage;
