// src/pages/ExercisePage.js
import React, { useState } from 'react';
import './ExercisePage.css';

const exerciseData = {
  "Cardiac Rehab": ["Chair Marching", "Step-Ups", "Leg Extensions"],
  "Neuro Rehab": ["Balance Training", "Gait Training", "Coordination"],
  "Orthopedic Rehab": ["Chair Squats", "Leg Raise", "Shoulder Rolls"],
  "Physiotherapy": ["Arm Circles", "Shoulder Rolls", "Hydration Tips"],
};

function ExercisePage() {
  const [selectedCategory, setSelectedCategory] = useState("Cardiac Rehab");
  const [selectedExercise, setSelectedExercise] = useState("Chair Marching");
  const [viewGraph, setViewGraph] = useState(false);
  const [isScanning, setIsScanning] = useState(false);

  const handleStart = () => {
    setIsScanning(true);
    console.log(`Start scanning for: ${selectedExercise}`);
  };

  const handleStop = () => {
    setIsScanning(false);
    console.log('Stop scanning');
  };

  const handleSave = () => {
    console.log('Data saved');
  };

  return (
    <div className="exercise-container">
      <header className="top-bar">
        <span>🤖 AI-Rehab</span>
        <span className="exercise-title">{selectedExercise}</span>
      </header>

      <div className="main-layout">
        <aside className="left-panel">
          <button
            className={`exercise-button graph-btn ${viewGraph ? 'active' : ''}`}
            onClick={() => setViewGraph(true)}
          >
            📊 View Graph
          </button>

          {Object.entries(exerciseData).map(([category, exercises]) => (
            <div key={category} className="category-group">
              <h4>{category}</h4>
              {exercises.map((exercise) => (
                <button
                  key={exercise}
                  className={`exercise-button ${selectedExercise === exercise ? 'active' : ''}`}
                  onClick={() => {
                    setSelectedExercise(exercise);
                    setSelectedCategory(category);
                    setViewGraph(false);
                  }}
                >
                  {exercise}
                </button>
              ))}
            </div>
          ))}

          <button className="go-back-btn" onClick={() => window.history.back()}>
            ← Go Back
          </button>
        </aside>

        <main className="content-panel">
          {viewGraph ? (
            <div className="graph-placeholder">
              📈 <br />
              <p>Progress Graph for {selectedExercise} (Placeholder)</p>
            </div>
          ) : (
            <>
              <div className="canvas-placeholder">
                [ Canvas/Webcam Feed for <strong>{selectedExercise}</strong> ]
              </div>
              <div className="controls">
                <button className="control-btn start" onClick={handleStart}>Start Scan</button>
                <button className="control-btn stop" onClick={handleStop}>Stop Scan</button>
                <button className="control-btn save" onClick={handleSave}>Save</button>
              </div>
            </>
          )}
        </main>
      </div>
    </div>
  );
}

export default ExercisePage;
