let lastPunch = null;
let lastSpoken = '';
let lastSpeakTime = 0;

export function resetSeatedArmPunch() {
  lastPunch = null;
  lastSpoken = '';
  lastSpeakTime = 0;
}

export function processSeatedArmPunch(landmarks) {
  const now = Date.now();
  const leftWrist = landmarks[15];
  const rightWrist = landmarks[16];
  const leftShoulder = landmarks[11];
  const rightShoulder = landmarks[12];

  if (!leftWrist || !rightWrist || !leftShoulder || !rightShoulder) {
    return { speak: 'Make sure your full arms are visible' };
  }

  const leftPunch = leftWrist.z < leftShoulder.z - 0.15;
  const rightPunch = rightWrist.z < rightShoulder.z - 0.15;

  if (leftPunch && lastPunch !== 'left') {
    lastPunch = 'left';
    return { speak: 'Left arm punch', rep: 1 };
  }

  if (rightPunch && lastPunch !== 'right') {
    lastPunch = 'right';
    return { speak: 'Right arm punch', rep: 1 };
  }

  if (!leftPunch && !rightPunch && now - lastSpeakTime > 4000) {
    lastSpeakTime = now;
    return { speak: 'Punch forward with one arm' };
  }

  return null;
}
