// src/pages/ExercisePage.js
import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ExercisePage.css';
import Chart from 'chart.js/auto';
import ChairSquatsLogic from '../exercises/ChairSquats'; // future: dynamically import based on selection

const exerciseData = {
  cardiac: ['Chair Marching', 'Leg Extensions', 'Chair Squats'],
  neuro: ['Balance Training', 'Gait Training', 'Coordination'],
  ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
  physio: ['Arm Circles', 'Shoulder Rolls', 'Hydration Tips'],
};

const ExercisePage = () => {
  const { type } = useParams();
  const [selectedExercise, setSelectedExercise] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [reps, setReps] = useState(0);
  const [showGraph, setShowGraph] = useState(false);
  const [poseScore, setPoseScore] = useState(0);
  const [timeElapsed, setTimeElapsed] = useState(0);
  const [intervalId, setIntervalId] = useState(null);

  const canvasRef = useRef(null);
  const videoRef = useRef(null);
  const chartRef = useRef(null);
  const detectorRef = useRef(null);

  const exercises = exerciseData[type] || [];

  // Start timer
  useEffect(() => {
    if (isScanning) {
      const id = setInterval(() => {
        setTimeElapsed((prev) => prev + 1);
      }, 1000);
      setIntervalId(id);
    } else {
      clearInterval(intervalId);
    }
  }, [isScanning]);

  // Pose detection logic
  useEffect(() => {
    if (!isScanning || !selectedExercise) return;

    const run = async () => {
      const { loadModel, detectPose } = ChairSquatsLogic; // Later: switch by name
      const detector = await loadModel();
      detectorRef.current = detector;

      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;

      videoRef.current.onloadeddata = () => {
        detectFrame();
      };
    };

    const detectFrame = async () => {
      if (!detectorRef.current) return;
      const poses = await detectorRef.current.estimatePoses(videoRef.current);
      drawPose(poses);
      const feedback = ChairSquatsLogic.validatePose(poses[0]);
      if (feedback.isValid) {
        setReps((r) => r + 1);
        speak('Good posture. Repetition counted.');
      } else {
        speak(feedback.message);
      }
      requestAnimationFrame(detectFrame);
    };

    const drawPose = (poses) => {
      const ctx = canvasRef.current.getContext('2d');
      ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

      poses.forEach((pose) => {
        pose.keypoints.forEach((kp) => {
          if (kp.score > 0.4) {
            ctx.beginPath();
            ctx.arc(kp.x, kp.y, 5, 0, 2 * Math.PI);
            ctx.fillStyle = 'lime';
            ctx.fill();
          }
        });
      });
    };

    run();
  }, [isScanning, selectedExercise]);

  const speak = (msg) => {
    const synth = window.speechSynthesis;
    if (!synth.speaking) {
      const utter = new SpeechSynthesisUtterance(msg);
      synth.speak(utter);
    }
  };

  const handleStart = () => {
    if (!selectedExercise) {
      alert('Please select an exercise');
      return;
    }
    setIsScanning(true);
    setTimeElapsed(0);
    setReps(0);
  };

  const handleStop = () => {
    setIsScanning(false);
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach((t) => t.stop());
    }
  };

  const handleSave = () => {
    alert(`Saved: ${reps} reps in ${timeElapsed} seconds`);
  };

  useEffect(() => {
    if (showGraph && chartRef.current) {
      const ctx = chartRef.current.getContext('2d');
      new Chart(ctx, {
        type: 'line',
        data: {
          labels: Array.from({ length: timeElapsed }, (_, i) => i + 1),
          datasets: [
            {
              label: 'Reps Over Time',
              data: Array.from({ length: timeElapsed }, (_, i) => (i < reps ? i + 1 : reps)),
              borderColor: 'rgba(75, 192, 192, 1)',
              fill: false,
            },
          ],
        },
      });
    }
  }, [showGraph]);

  return (
    <div className="exercise-container">
      <header className="top-bar">
        AI-Rehab ‚Äì {selectedExercise || 'Select Exercise'}
      </header>

      <div className="main-layout">
        <aside className="left-panel">
          {exercises.map((exercise) => (
            <button
              key={exercise}
              className={`exercise-button ${selectedExercise === exercise ? 'active' : ''}`}
              onClick={() => {
                setSelectedExercise(exercise);
                setShowGraph(false);
              }}
            >
              {exercise}
            </button>
          ))}
          <button
            className="exercise-button graph-button"
            onClick={() => {
              setShowGraph(true);
              setIsScanning(false);
            }}
          >
            üìà View Graph
          </button>
        </aside>

        <main className="content-panel">
          {showGraph ? (
            <div className="canvas-placeholder graph-view">
              <canvas ref={chartRef} width="500" height="300" />
            </div>
          ) : (
            <div className="canvas-placeholder">
              <video ref={videoRef} width="640" height="480" autoPlay muted />
              <canvas ref={canvasRef} width="640" height="480" className="absolute top-0 left-0" />
            </div>
          )}

          {!showGraph && (
            <div className="controls">
              <button className="control-btn start" onClick={handleStart}>Start Scan</button>
              <button className="control-btn stop" onClick={handleStop}>Stop Scan</button>
              <button className="control-btn save" onClick={handleSave}>Save</button>
              <div className="text-sm mt-3 text-gray-500">
                ‚è± {timeElapsed}s | üîÅ {reps} reps
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default ExercisePage;
