// src/pages/ExercisePage.js
import React, { useEffect, useRef, useState } from 'react';
import { useParams } from 'react-router-dom';
import './ExercisePage.css';
import * as tf from '@tensorflow/tfjs';
import * as posedetection from '@tensorflow-models/pose-detection';
import '@tensorflow/tfjs-backend-webgl';
import { processChairSquat, resetChairSquat } from '../exercises/ChairSquats';

const ExercisePage = () => {
  const { type } = useParams();
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const detectorRef = useRef(null);
  const speakRef = useRef(window.speechSynthesis);
  const [selectedExercise, setSelectedExercise] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [repCount, setRepCount] = useState(0);
  const [poseStatus, setPoseStatus] = useState('');
  const [timer, setTimer] = useState(0);
  const [intervalId, setIntervalId] = useState(null);

  const exerciseData = {
    cardiac: ['Chair Marching', 'Leg Extensions', 'Step-Ups'],
    neuro: ['Balance Training', 'Gait Training', 'Coordination'],
    ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
    physio: ['Arm Circles', 'Shoulder Rolls', 'Hydration Tips'],
  };

  const exercises = exerciseData[type] || [];

  const speak = (text) => {
    if (!speakRef.current.speaking) {
      const utter = new SpeechSynthesisUtterance(text);
      speakRef.current.speak(utter);
    }
  };

  const startTimer = () => {
    const id = setInterval(() => {
      setTimer((prev) => prev + 1);
    }, 1000);
    setIntervalId(id);
  };

  const stopTimer = () => {
    clearInterval(intervalId);
    setIntervalId(null);
  };

  const reset = () => {
    setRepCount(0);
    setPoseStatus('');
    setTimer(0);
    resetChairSquat();
  };

  const handleStart = () => {
    if (!selectedExercise) return alert('Select an exercise first!');
    setIsScanning(true);
    reset();
    startTimer();
  };

  const handleStop = () => {
    setIsScanning(false);
    stopTimer();
    if (speakRef.current.speaking) speakRef.current.cancel();
  };

  const handleSave = () => {
    alert(`Saved ${repCount} reps in ${timer} sec`);
  };

  useEffect(() => {
    const runPose = async () => {
      await tf.setBackend('webgl');
      detectorRef.current = await posedetection.createDetector(posedetection.SupportedModels.MoveNet, {
        modelType: posedetection.movenet.modelType.SINGLEPOSE_LIGHTNING,
      });

      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.onloadedmetadata = () => videoRef.current.play();
      }
    };

    runPose();
  }, []);

  useEffect(() => {
    const detect = async () => {
      if (
        !isScanning ||
        !videoRef.current ||
        videoRef.current.readyState !== 4 ||
        !detectorRef.current
      ) {
        requestAnimationFrame(detect);
        return;
      }

      const poses = await detectorRef.current.estimatePoses(videoRef.current);
      drawResults(poses);

      if (selectedExercise === 'Chair Squats') {
        const { isCorrect, count, feedback } = processChairSquat(poses[0]);
        if (count > repCount) setRepCount(count);
        setPoseStatus(feedback);

        if (!isCorrect) {
          speak('Posture incorrect');
        } else if (feedback === 'Posture correct') {
          speak('Posture correct');
        }
      }

      requestAnimationFrame(detect);
    };

    const drawResults = (poses) => {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      poses.forEach((pose) => {
        pose.keypoints.forEach((keypoint) => {
          if (keypoint.score > 0.4) {
            ctx.beginPath();
            ctx.arc(keypoint.x, keypoint.y, 5, 0, 2 * Math.PI);
            ctx.fillStyle = 'lime';
            ctx.fill();
          }
        });
      });
    };

    detect();
  }, [isScanning, selectedExercise]);

  return (
    <div className="exercise-container">
      <header className="top-bar">AI-Rehab â€“ {selectedExercise || 'Select Exercise'}</header>
      <div className="main-layout">
        <aside className="left-panel">
          {exercises.map((exercise) => (
            <button
              key={exercise}
              className={`exercise-button ${selectedExercise === exercise ? 'active' : ''}`}
              onClick={() => {
                setSelectedExercise(exercise);
                reset();
              }}
            >
              {exercise}
            </button>
          ))}
        </aside>

        <main className="content-panel">
          <div className="canvas-wrapper">
            <video ref={videoRef} width="640" height="480" style={{ display: 'none' }} />
            <canvas ref={canvasRef} width="640" height="480" />
          </div>

          <div className="controls">
            <p><strong>Status:</strong> {poseStatus}</p>
            <p><strong>Reps:</strong> {repCount}</p>
            <p><strong>Time:</strong> {timer}s</p>
            <button className="control-btn start" onClick={handleStart}>Start Scan</button>
            <button className="control-btn stop" onClick={handleStop}>Stop Scan</button>
            <button className="control-btn save" onClick={handleSave}>Save</button>
          </div>
        </main>
      </div>
    </div>
  );
};

export default ExercisePage;
