import React, { useState } from 'react';
import './ExercisePage.css';

const exerciseGroups = {
  cardiac: ['Step-Ups', 'Chair Marching', 'Leg Extensions'],
  neuro: ['Balance Training', 'Gait Training', 'Coordination'],
  ortho: ['Chair Squats', 'Leg Raise', 'Shoulder Rolls'],
  physio: ['Hydration', 'Proper Form', 'Rest'],
};

function ExercisePage() {
  const [selectedExercise, setSelectedExercise] = useState('Step-Ups');
  const [isScanning, setIsScanning] = useState(false);
  const [showGraph, setShowGraph] = useState(false);

  const handleStart = () => {
    setIsScanning(true);
    console.log(`Start scanning: ${selectedExercise}`);
  };

  const handleStop = () => {
    setIsScanning(false);
    console.log('Stop scanning');
  };

  const handleSave = () => {
    console.log('Saved');
  };

  return (
    <div className="exercise-container min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-white">
      <header className="bg-gray-800 text-white px-6 py-4 flex justify-between items-center shadow-md">
        <h2 className="text-xl font-semibold">ðŸ’¡ AI-Rehab</h2>
        <h3 className="text-md font-medium">{selectedExercise}</h3>
      </header>

      <div className="flex">
        {/* Left panel */}
        <aside className="w-64 bg-gray-200 dark:bg-gray-800 p-4 space-y-3">
          <h3 className="text-lg font-semibold mb-3">Exercises</h3>
          {Object.entries(exerciseGroups).map(([group, items]) =>
            items.map((exercise) => (
              <button
                key={exercise}
                className={`w-full px-4 py-2 rounded text-left font-medium ${
                  selectedExercise === exercise
                    ? 'bg-blue-600 text-white'
                    : 'bg-white dark:bg-gray-700 hover:bg-blue-100 dark:hover:bg-gray-600'
                }`}
                onClick={() => {
                  setSelectedExercise(exercise);
                  setShowGraph(false);
                }}
              >
                {exercise}
              </button>
            ))
          )}
          <button
            onClick={() => setShowGraph(true)}
            className="mt-4 w-full px-4 py-2 bg-yellow-400 text-black font-semibold rounded hover:bg-yellow-500"
          >
            ðŸ“Š View Graph
          </button>
        </aside>

        {/* Main content */}
        <main className="flex-1 p-6">
          {showGraph ? (
            <div className="w-full h-96 bg-yellow-100 dark:bg-yellow-800 rounded shadow-md flex items-center justify-center text-center">
              <p className="text-lg font-semibold text-gray-700 dark:text-white">
                ðŸ“ˆ Progress Graph (Placeholder)
              </p>
            </div>
          ) : (
            <>
              <div className="w-full h-96 bg-blue-100 border-2 border-dashed border-blue-400 rounded-lg flex items-center justify-center mb-6">
                <p className="text-blue-800 font-semibold">
                  [ Canvas/Webcam Feed for {selectedExercise} ]
                </p>
              </div>

              <div className="flex gap-4">
                <button
                  className="bg-green-600 hover:bg-green-700 text-white px-5 py-2 rounded"
                  onClick={handleStart}
                >
                  Start Scan
                </button>
                <button
                  className="bg-red-600 hover:bg-red-700 text-white px-5 py-2 rounded"
                  onClick={handleStop}
                >
                  Stop Scan
                </button>
                <button
                  className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded"
                  onClick={handleSave}
                >
                  Save
                </button>
              </div>
            </>
          )}
        </main>
      </div>
    </div>
  );
}

export default ExercisePage;
